let columns = 0;

for(var series in msg.payload[0]) {
    if(series!=="time") {
        chart[0].series.push(series);
        chart[0].data.push([]);
        columns++;
    }
}


for (var j=0; j<msg.payload.length; j++) {
   for(var i=0;i<columns;i++) {
       chart[0].data[i].push({"x":msg.payload[j].time, "y":msg.payload[j][chart[0].series[i]]});
   } 
}




msg.payload = chart;

return msg;

let folder = flow.get("folder") || "/";
flow.set("folder", folder);

let hidden = flow.get("hidden") || false;
flow.set("hidden", hidden);

if (msg.topic==="parent") {
    var path = folder.split('/');
    path.pop();
    folder = path.join('/'); 
    if (folder==="") folder="/";
    flow.set("folder", folder);
}

if (msg.topic==="change") {
    folder=msg.payload;    
    flow.set("folder", folder);
}

if (msg.topic==="hidden") {
    hidden=msg.payload;    
    flow.set("hidden", hidden);
}

//if (msg.topic==="refresh") {
//}

msg.payload = {"start":folder, "hidden": hidden};
return msg;

for (var i = 0; i < msg.payload.length; i++) {
